const questions_hoa_topic4 = [
{q:"Để chứng minh trong một mẫu bánh mì có chứa tinh bột, người ta có thể dùng thuốc thử nào?",options:["A. Dung dịch iodine","B. Dung dịch Bromine","C. Dung dịch NaOH","D. Dung dịch HCl"],a:"A. Dung dịch iodine",explain:"Tinh bột tạo phức màu xanh tím đặc trưng với iodine.", level: "van_dung"},
{q:"Khi nhai kỹ cơm (chứa tinh bột) thấy có vị ngọt, điều này chứng tỏ điều gì?",options:["A. Tinh bột bị thủy phân một phần thành maltose nhờ enzyme amylase trong nước bọt","B. Tinh bột bị oxi hóa thành đường glucose","C. Tinh bột bị lên men tạo Ethyl alcohol (ethanol)","D. Cơm có sẵn đường sucrose"],a:"A. Tinh bột bị thủy phân một phần thành maltose nhờ enzyme amylase trong nước bọt",explain:"Enzyme amylase trong nước bọt thủy phân tinh bột thành đường maltose có vị ngọt.", level: "van_dung"},
{q:"Để phân biệt dầu ăn (chất béo lỏng) và nước, có thể dùng phương pháp nào?",options:["A. Cho vào nước, quan sát độ tan","B. Đun nóng, quan sát nhiệt độ sôi","C. Thử với dung dịch iodine","D. Thử với dung dịch NaOH"],a:"A. Cho vào nước, quan sát độ tan",explain:"Dầu ăn không tan trong nước, nổi lên trên tạo lớp riêng biệt.", level: "van_dung"},
{q:"Một học sinh muốn chứng minh trong lòng trắng trứng có chứa protein. Có thể dùng phản ứng nào?",options:["A. Phản ứng màu biure","B. Phản ứng với iodine","C. Phản ứng với Bromine","D. Phản ứng với HCl"],a:"A. Phản ứng màu biure",explain:"Protein tạo phức màu tím đặc trưng với Cu(OH)₂ trong môi trường kiềm (phản ứng biure).", level: "van_dung"},
{q:"Khi đun nóng đường sucrose với dung dịch H₂SO₄ loãng, sản phẩm thu được có thể làm đục nước vôi trong. Giải thích nào đúng?",options:["A. Sucrose bị thủy phân thành glucose và fructose, sau đó bị oxi hóa tạo CO₂","B. Sucrose bị oxi hóa trực tiếp tạo CO₂","C. Sucrose bị khử tạo carbon","D. H₂SO₄ phân hủy tạo SO₂"],a:"A. Sucrose bị thủy phân thành glucose và fructose, sau đó bị oxi hóa tạo CO₂",explain:"Acid xúc tác thủy phân sucrose, các monosaccharide sinh ra bị oxi hóa tiếp tạo CO₂.", level: "van_dung"},
{q:"Để tách riêng dầu ăn ra khỏi hỗn hợp với nước, phương pháp nào hiệu quả nhất?",options:["A. Chiết","B. Chưng cất","C. Lọc","D. Kết tinh"],a:"A. Chiết",explain:"Dầu và nước không trộn lẫn, tách thành 2 lớp, có thể chiết tách.", level: "van_dung"},
{q:"Một người bị bệnh tiểu đường cần hạn chế thực phẩm giàu chất nào nhất?",options:["A. Carbohydrate (đường, tinh bột)","B. Protein","C. Lipid","D. Vitamin"],a:"A. Carbohydrate (đường, tinh bột)",explain:"Carbohydrate làm tăng đường huyết nhanh, cần kiểm soát ở người tiểu đường.", level: "van_dung"},
{q:"Khi để lâu ngoài không khí, dầu ăn thường bị ôi thiu. Nguyên nhân chính là do?",options:["A. Chất béo bị oxi hóa bởi oxygen không khí","B. Chất béo bị thủy phân bởi hơi nước","C. Chất béo bị lên men","D. Chất béo bị bay hơi"],a:"A. Chất béo bị oxi hóa bởi oxygen không khí",explain:"Các nối đôi trong acid béo không no bị oxi hóa tạo sản phẩm có mùi khó chịu.", level: "van_dung"},
{q:"Để bảo quản chất béo được lâu, người ta thường cho thêm chất chống oxi hóa. Chất này có tác dụng gì?",options:["A. Ngăn cản quá trình oxi hóa chất béo","B. Ngăn cản quá trình thủy phân chất béo","C. Diệt vi khuẩn","D. Hút ẩm"],a:"A. Ngăn cản quá trình oxi hóa chất béo",explain:"Chất chống oxi hóa ngăn cản phản ứng oxi hóa các nối đôi trong acid béo.", level: "van_dung"},
{q:"Khi thủy phân hoàn toàn một chất béo (triglyceride) bằng NaOH, sản phẩm thu được là gì?",options:["A. Glyxerol và muối của acid béo (xà phòng)","B. Acid béo và glyxerol","C. Glyxerol và alcohol","D. Acid béo và muối"],a:"A. Glyxerol và muối của acid béo (xà phòng)",explain:"Chất béo + NaOH → glyxerol + muối của acid béo (xà phòng).", level: "van_dung"},
{q:"Hiện tượng nào xảy ra khi nhỏ vài giọt dung dịch iodine vào lát chuối xanh?",options:["A. Xuất hiện màu xanh tím","B. Không đổi màu","C. Mất màu iodine","D. Xuất hiện màu vàng"],a:"A. Xuất hiện màu xanh tím",explain:"Chuối xanh chứa tinh bột, tạo phức màu xanh tím với iodine.", level: "van_dung"},
{q:"Tại sao khi nấu cơm, tinh bột trong gạo trở nên dễ tiêu hóa hơn?",options:["A. Nhiệt độ làm hồ hóa tinh bột, phá vỡ cấu trúc hạt","B. Nhiệt độ thủy phân hoàn toàn tinh bột thành đường","C. Nhiệt độ làm bay hơi nước","D. Nhiệt độ làm chết enzyme phân hủy tinh bột"],a:"A. Nhiệt độ làm hồ hóa tinh bột, phá vỡ cấu trúc hạt",explain:"Quá trình hồ hóa làm tinh bột trương nở, dễ tiếp xúc với enzyme tiêu hóa.", level: "van_dung"},
{q:"Để chứng minh sự có mặt của đường khử (như glucose) trong nước tiểu của bệnh nhân tiểu đường, có thể dùng thuốc thử nào?",options:["A. Dung dịch Fehling hoặc Tollens","B. Dung dịch iodine","C. Dung dịch Bromine","D. Dung dịch NaOH"],a:"A. Dung dịch Fehling hoặc Tollens",explain:"Đường khử khử được Cu²⁺ trong Fehling thành Cu₂O↓ đỏ gạch, hoặc Ag⁺ trong Tollens thành Ag↓.", level: "van_dung"},
{q:"Khi cho protein tác dụng với acid đặc nóng, sản phẩm cuối cùng thu được chủ yếu là gì?",options:["A. Hỗn hợp các α-amino acid","B. Carbon dioxide và nước","C. Các peptide ngắn","D. Ammonia và acid carboxylic"],a:"A. Hỗn hợp các α-amino acid",explain:"Protein bị thủy phân hoàn toàn trong môi trường acid tạo các amino acid.", level: "van_dung"},
{q:"Polyme thiên nhiên nào sau đây có cấu trúc mạch không phân nhánh?",options:["A. Amylose (thành phần của tinh bột)","B. Amylopectin","C. Glycogen","D. Tất cả đều phân nhánh"],a:"A. Amylose (thành phần của tinh bột)",explain:"Amylose có cấu trúc mạch thẳng, không phân nhánh.", level: "van_dung"},
{q:"Tại sao các đồ vật bằng nhựa (polyme) rất khó phân hủy trong tự nhiên?",options:["A. Do có cấu trúc bền vững, vi sinh vật khó phân hủy","B. Do không tan trong nước","C. Do có khối lượng phân tử thấp","D. Do có màu sắc bắt mắt"],a:"A. Do có cấu trúc bền vững, vi sinh vật khó phân hủy",explain:"Các liên kết trong polyme tổng hợp bền, không có enzyme tự nhiên phân hủy hiệu quả.", level: "van_dung"},
{q:"Để giảm thiểu ô nhiễm từ túi nilon (polyethylene), giải pháp nào khả thi nhất?",options:["A. Sử dụng túi phân hủy sinh học","B. Đốt bỏ túi nilon","C. Chôn lấp sâu","D. Vứt xuống sông, biển"],a:"A. Sử dụng túi phân hủy sinh học",explain:"Túi phân hủy sinh học được làm từ nguyên liệu có thể phân hủy bởi vi sinh vật.", level: "van_dung"},
{q:"Khi cho glyxerol tác dụng với copper(II) hydroxide, hiện tượng xảy ra là gì?",options:["A. Tạo dung dịch màu xanh lam đậm","B. Tạo kết tủa đỏ gạch","C. Tạo kết tủa trắng","D. Không có hiện tượng"],a:"A. Tạo dung dịch màu xanh lam đậm",explain:"Glyxerol hòa tan Cu(OH)₂ tạo phức màu xanh lam.", level: "van_dung"},
{q:"Một chất hữu cơ X khi thủy phân trong môi trường acid tạo ra glyxerol và hỗn hợp acid béo. X có thể là chất nào?",options:["A. Chất béo (triglyceride)","B. Protein","C. Carbohydrate","D. Polyme"],a:"A. Chất béo (triglyceride)",explain:"Chất béo thủy phân tạo glyxerol và acid béo.", level: "van_dung"},
{q:"Khi đun nóng protein với dung dịch NaOH rồi thêm vài giọt CuSO₄, xuất hiện màu tím. Đây là phản ứng gì?",options:["A. Phản ứng biure","B. Phản ứng Fehling","C. Phản ứng Tollens","D. Phản ứng với iodine"],a:"A. Phản ứng biure",explain:"Protein tạo phức màu tím với ion Cu²⁺ trong môi trường kiềm (phản ứng biure).", level: "van_dung"},
{q:"Tại sao người ta thường cho thêm enzyme vào bột giặt?",options:["A. Để phân hủy các vết bẩn protein, chất béo","B. Để tạo bọt nhiều hơn","C. Để làm trắng quần áo","D. Để tạo mùi thơm"],a:"A. Để phân hủy các vết bẩn protein, chất béo",explain:"Enzyme protease phân hủy protein, lipase phân hủy chất béo trong vết bẩn.", level: "van_dung"},
{q:"Một học sinh có ba mẫu bột: bột gạo (tinh bột), bột đậu (protein), bột mỡ (lipid). Có thể dùng trình tự thuốc thử nào để phân biệt?",options:["A. Iodine → NaOH + CuSO₄ → tan trong nước","B. Bromine → Fehling → iodine","C. HCl → NaOH → Bromine","D. Nước → iodine → Bromine"],a:"A. Iodine → NaOH + CuSO₄ → tan trong nước",explain:"Iodine nhận tinh bột (xanh tím). Phản ứng biure (NaOH+CuSO₄) nhận protein (tím). Lipid không tan trong nước, nổi.", level: "van_dung"},
{q:"Polyme nào sau đây được điều chế bằng phản ứng trùng hợp?",options:["A. Polyethylene","B. Polyester","C. Nylon-6,6","D. Tơ visco"],a:"A. Polyethylene",explain:"Polyethylene được trùng hợp từ ethylene, các chất khác được điều chế bằng phản ứng trùng ngưng.", level: "van_dung"},
{q:"Khi cho glucose tác dụng với dung dịch AgNO₃ trong NH₃, hiện tượng xảy ra là gì?",options:["A. Tạo kết tủa bạc màu trắng bạc","B. Tạo dung dịch màu xanh","C. Tạo kết tủa đỏ gạch","D. Không có hiện tượng"],a:"A. Tạo kết tủa bạc màu trắng bạc",explain:"Glucose khử Ag⁺ thành Ag (phản ứng tráng gương).", level: "van_dung"},
{q:"Để chuyển hóa dầu thực vật (lỏng) thành mỡ rắn nhân tạo, người ta thực hiện quá trình nào?",options:["A. Hydrogen hóa","B. Oxi hóa","C. Thủy phân","D. Este hóa"],a:"A. Hydrogen hóa",explain:"Hydrogen hóa nối đôi trong acid béo không no, làm tăng nhiệt độ nóng chảy.", level: "van_dung"},
{q:"Tại sao khi luộc trứng, lòng trắng trứng chuyển từ trong suốt thành đục trắng?",options:["A. Protein bị biến tính bởi nhiệt","B. Protein bị thủy phân","C. Lipid bị nóng chảy","D. Nước bay hơi"],a:"A. Protein bị biến tính bởi nhiệt",explain:"Nhiệt độ làm mất cấu trúc không gian bậc 2,3 của protein, gây đông tụ.", level: "van_dung"},
{q:"Một loại polymer có thể bị thủy phân trong môi trường acid tạo thành các monomer glucose. Polymer đó là gì?",options:["A. Tinh bột hoặc cellulose","B. Protein","C. Chất béo","D. Polyethylene"],a:"A. Tinh bột hoặc cellulose",explain:"Cả tinh bột và cellulose đều là polyme của glucose.", level: "van_dung"},
{q:"Để làm mềm vải sợi bông (cellulose) bị cứng, người ta thường ngâm trong dung dịch acid acetic loãng. Giải thích nào đúng?",options:["A. Acid trung hòa base còn sót lại từ quá trình giặt","B. Acid thủy phân cellulose","C. Acid làm tan chất bẩn","D. Acid tạo phức với cellulose"],a:"A. Acid trung hòa base còn sót lại từ quá trình giặt",explain:"Xà phòng có tính base có thể làm cứng sợi bông, acid acetic trung hòa base này.", level: "van_dung"},
{q:"Khi để mật ong lâu ngày, đường glucose có thể kết tinh. Để hạn chế hiện tượng này, nên bảo quản thế nào?",options:["A. Bảo quản ở nhiệt độ thấp","B. Bảo quản ở nhiệt độ cao","C. Phơi nắng","D. Thêm nước"],a:"A. Bảo quản ở nhiệt độ thấp",explain:"Nhiệt độ thấp làm giảm tốc độ kết tinh của đường.", level: "van_dung"},
{q:"Để kiểm tra một mẫu sữa có bị pha loãng với nước hay không, người ta có thể dùng phương pháp nào đơn giản?",options:["A. Đo khối lượng riêng","B. Thử với dung dịch iodine","C. Thử với dung dịch Bromine","D. Đun sôi"],a:"A. Đo khối lượng riêng",explain:"Sữa nguyên chất có khối lượng riêng khoảng 1,03 g/ml, khi pha nước sẽ giảm.", level: "van_dung"},
{q:"Khi làm bánh mì, người ta thường cho thêm men nở. Vai trò của men nở là gì?",options:["A. Lên men đường tạo CO₂ làm bánh nở","B. Thủy phân tinh bột thành đường","C. Tạo hương vị cho bánh","D. Làm bánh mềm hơn"],a:"A. Lên men đường tạo CO₂ làm bánh nở",explain:"Men nở (yeast) lên men đường tạo khí CO₂ tạo bọt khí làm bánh nở.", level: "van_dung"},
{q:"Tại sao khi bảo quản mỡ lợn người ta thường cho thêm muối?",options:["A. Muối ức chế vi khuẩn, chống ôi thiu","B. Muối làm tăng hương vị","C. Muối hút ẩm","D. Muối làm mỡ cứng hơn"],a:"A. Muối ức chế vi khuẩn, chống ôi thiu",explain:"Muối tạo môi trường ưu trương ức chế sự phát triển của vi khuẩn gây ôi thiu.", level: "van_dung"},
{q:"Để xác định một mẫu chất béo có chứa glyxerol, có thể dùng phản ứng nào?",options:["A. Tạo phức màu xanh với Cu(OH)₂","B. Làm mất màu Bromine","C. Tạo màu tím với ninhydrin","D. Tạo kết tủa vàng với iodine"],a:"A. Tạo phức màu xanh với Cu(OH)₂",explain:"Glyxerol hòa tan Cu(OH)₂ tạo dung dịch phức màu xanh lam đậm.", level: "van_dung"},
{q:"Khi thủy phân saccarose trong môi trường acid, thu được hỗn hợp glucose và fructose. Hỗn hợp này có tính chất gì đặc biệt?",options:["A. Làm quay mặt phẳng ánh sáng phân cực sang trái","B. Làm quay mặt phẳng ánh sáng phân cực sang phải","C. Không làm quay mặt phẳng ánh sáng phân cực","D. Làm đảo ngược chiều quay"],a:"C. Không làm quay mặt phẳng ánh sáng phân cực",explain:"Hỗn hợp glucose (quay phải) và fructose (quay trái mạnh hơn) tạo hỗn hợp không quay.", level: "van_dung"},
{q:"Tại sao người bị bệnh gout cần hạn chế thực phẩm giàu protein như thịt đỏ, hải sản?",options:["A. Protein phân hủy tạo acid uric, tích tụ gây bệnh gout","B. Protein gây dị ứng","C. Protein làm tăng cholesterol","D. Protein khó tiêu hóa"],a:"A. Protein phân hủy tạo acid uric, tích tụ gây bệnh gout",explain:"Purine trong protein phân hủy thành acid uric, lắng đọng ở khớp gây gout.", level: "van_dung"},
{q:"Để tẩy vết mực trên quần áo, người ta thường dùng sữa tươi. Giải thích nào đúng?",options:["A. Casein trong sữa hòa tan mực","B. Lipid trong sữa hòa tan mực","C. Đường trong sữa phản ứng với mực","D. Sữa có tính oxi hóa mạnh"],a:"A. Casein trong sữa hòa tan mực",explain:"Casein (protein trong sữa) có tính chất như chất hoạt động bề mặt, hòa tan chất màu trong mực.", level: "van_dung"},
{q:"Khi cho Bromine vào dầu ăn, màu Bromine nhạt dần. Điều này chứng tỏ điều gì về dầu ăn?",options:["A. Dầu ăn chứa acid béo không no","B. Dầu ăn chứa glyxerol","C. Dầu ăn chứa protein","D. Dầu ăn chứa đường"],a:"A. Dầu ăn chứa acid béo không no",explain:"Nối đôi trong acid béo không no cộng với Bromine làm mất màu dung dịch Bromine.", level: "van_dung"},
{q:"Để làm sạch dầu mỡ bám trên chảo, người ta thường dùng nước rửa chén. Nước rửa chén có tác dụng gì?",options:["A. Nhũ hóa chất béo, tạo nhũ tương với nước","B. Hòa tan hoàn toàn chất béo","C. Oxi hóa chất béo thành chất tan","D. Thủy phân hoàn toàn chất béo"],a:"A. Nhũ hóa chất béo, tạo nhũ tương với nước",explain:"Chất hoạt động bề mặt trong nước rửa chén làm giảm sức căng bề mặt, nhũ hóa chất béo.", level: "van_dung"},
{q:"Tại sao khi bị ong đốt, người ta thường bôi vôi hoặc dung dịch ammonia lên vết đốt?",options:["A. Trung hòa acid formic trong nọc ong","B. Sát trùng vết thương","C. Giảm đau, giảm sưng","D. Oxi hóa chất độc"],a:"A. Trung hòa acid formic trong nọc ong",explain:"Nọc ong chứa acid formic, base (NH₃, Ca(OH)₂) trung hòa acid giảm đau.", level: "van_dung"},
{q:"Một học sinh có ba ống nghiệm chứa: glucose, saccarose, tinh bột. Có thể dùng trình tự thuốc thử nào để phân biệt?",options:["A. Dung dịch AgNO₃/NH₃ → đun với HCl rồi thử AgNO₃/NH₃ → iodine","B. Iodine → Bromine → NaOH","C. Cu(OH)₂ → Bromine → HCl","D. NaOH → iodine → Bromine"],a:"A. Dung dịch AgNO₃/NH₃ → đun với HCl rồi thử AgNO₃/NH₃ → iodine",explain:"AgNO₃/NH₃ nhận glucose (tráng gương). Đun với HCl thủy phân saccarose thành đường khử, thử lại AgNO₃. Iodine nhận tinh bột (xanh tím).", level: "van_dung"},
{q:"Để bảo quản trái cây tươi lâu, người ta thường bảo quản trong môi trường có nồng độ CO₂ cao. Tác dụng của CO₂ là gì?",options:["A. Ức chế hô hấp của trái cây","B. Diệt vi khuẩn","C. Tạo môi trường acid","D. Hút ẩm"],a:"A. Ức chế hô hấp của trái cây",explain:"CO₂ cao làm giảm cường độ hô hấp, làm chậm quá trình chín và thối rữa.", level: "van_dung"},
{q:"Khi cho giấm ăn (acid acetic) vào sữa tươi, sữa bị vón cục. Giải thích nào đúng?",options:["A. Acid làm biến tính casein trong sữa","B. Acid thủy phân đường lactose","C. Acid oxi hóa chất béo","D. Acid tạo muối tan"],a:"A. Acid làm biến tính casein trong sữa",explain:"Casein (protein trong sữa) bị đông tụ ở pH thấp (môi trường acid).", level: "van_dung"},
{q:"Để kiểm tra một loại đường có phải là đường khử hay không, ngoài phản ứng tráng gương còn có thể dùng phản ứng nào?",options:["A. Với dung dịch Fehling","B. Với dung dịch Bromine","C. Với dung dịch iodine","D. Với dung dịch NaOH"],a:"A. Với dung dịch Fehling",explain:"Đường khử khử Cu²⁺ trong Fehling thành Cu₂O↓ đỏ gạch.", level: "van_dung"},
{q:"Tại sao khi ăn nhiều thực phẩm chiên rán lại không tốt cho sức khỏe?",options:["A. Dầu mỡ tái sử dụng nhiều lần có thể tạo chất độc","B. Thực phẩm chiên rán mất vitamin","C. Dầu mỡ bị oxi hóa tạo chất có hại","D. Tất cả các ý trên"],a:"D. Tất cả các ý trên",explain:"Chiên rán nhiều lần làm dầu bị oxi hóa, phân hủy tạo chất độc, Copper thời mất chất dinh dưỡng.", level: "van_dung"},
{q:"Một loại polymer khi đốt cháy có mùi khét như tóc cháy. Polymer đó có thể là gì?",options:["A. Protein (tơ tằm, len)","B. Polyethylene","C. Polystyrene","D. Polyvinyl chloride"],a:"A. Protein (tơ tằm, len)",explain:"Protein chứa nitrogen, khi cháy tạo mùi khét đặc trưng.", level: "van_dung"},
{q:"Để làm sạch nước có lẫn dầu mỡ trong các sự cố tràn dầu, người ta có thể dùng biện pháp nào?",options:["A. Dùng vi sinh vật phân hủy dầu","B. Dùng hóa chất hòa tan dầu","C. Dùng nam châm hút dầu","D. Đun sôi nước"],a:"A. Dùng vi sinh vật phân hủy dầu",explain:"Vi sinh vật có enzyme phân hủy hydrocarbon trong dầu thành chất ít độc hơn.", level: "van_dung"},
{q:"Khi cho H₂SO₄ đặc vào đường saccarose, đường bị hóa than. Hiện tượng này do nguyên nhân chính nào?",options:["A. H₂SO₄ đặc hút nước mạnh, làm đường bị than hóa","B. H₂SO₄ oxi hóa đường thành carbon","C. H₂SO₄ thủy phân đường","D. H₂SO₄ tạo phức với đường"],a:"A. H₂SO₄ đặc hút nước mạnh, làm đường bị than hóa",explain:"H₂SO₄ đặc có tính háo nước mạnh, tách nước khỏi phân tử đường.", level: "van_dung"},
{q:"Tại sao khi làm kem, người ta thường cho thêm lòng đỏ trứng?",options:["A. Lecithin trong lòng đỏ trứng có tính nhũ hóa","B. Tạo màu vàng đẹp","C. Tạo hương vị thơm ngon","D. Cung cấp protein"],a:"A. Lecithin trong lòng đỏ trứng có tính nhũ hóa",explain:"Lecithin là chất nhũ hóa tự nhiên, giúp ổn định nhũ tương nước-dầu trong kem.", level: "van_dung"},
{q:"Để phân biệt tinh bột và cellulose (cả hai đều là polyme của glucose), có thể dựa vào điểm nào?",options:["A. Phản ứng với iodine","B. Độ tan trong nước","C. Phản ứng thủy phân","D. Màu sắc"],a:"A. Phản ứng với iodine",explain:"Tinh bột tạo màu xanh tím với iodine, cellulose không phản ứng.", level: "van_dung"},
{q:"Khi cho enzyme catalase (có trong gan) vào dung dịch H₂O₂, hiện tượng xảy ra là gì?",options:["A. Có khí oxy thoát ra mạnh","B. Dung dịch chuyển màu","C. Có kết tủa tạo thành","D. Không có hiện tượng"],a:"A. Có khí oxy thoát ra mạnh",explain:"Catalase xúc tác phân hủy H₂O₂ thành H₂O và O₂.", level: "van_dung"},
{q:"Tại sao khi nấu thịt với đậu (đậu đỏ, đậu đen) lại làm thịt mau mềm hơn?",options:["A. Enzyme trong đậu thủy phân protein trong thịt","B. Chất xơ trong đậu làm mềm thịt","C. Đậu hút bớt mỡ thừa","D. Đậu tạo môi trường acid làm mềm thịt"],a:"A. Enzyme trong đậu thủy phân protein trong thịt",explain:"Một số loại đậu chứa enzyme protease giúp phân cắt protein, làm thịt mềm.", level: "van_dung"},
{q:"Để kiểm tra một loại bột có phải là bột sắn (tinh bột) hay bột đá (CaCO₃), có thể dùng phương pháp nào?",options:["A. Hòa tan trong nước rồi nhỏ iodine","B. Cho tác dụng với HCl","C. Đốt trên ngọn lửa","D. Cho tác dụng với NaOH"],a:"A. Hòa tan trong nước rồi nhỏ iodine",explain:"Tinh bột tạo màu xanh tím với iodine, CaCO₃ không phản ứng.", level: "van_dung"},
{q:"Khi để lâu, mật ong có thể lên men tạo ra chất gì?",options:["A. Ethyl alcohol (ethanol) và CO₂","B. Acid lactic","C. Acid acetic","D. MEthyl alcohol (ethanol)"],a:"A. Ethyl alcohol (ethanol) và CO₂",explain:"Đường trong mật ong bị men tự nhiên lên men tạo Ethyl alcohol (ethanol) và CO₂.", level: "van_dung"},
{q:"Để tách cellulose từ bông, người ta thường xử lý với dung dịch NaOH. NaOH có tác dụng gì?",options:["A. Loại bỏ tạp chất như lignin, pectin","B. Thủy phân cellulose","C. Làm trắng bông","D. Làm mềm sợi bông"],a:"A. Loại bỏ tạp chất như lignin, pectin",explain:"NaOH hòa tan các tạp chất, để lại cellulose tương đối tinh khiết.", level: "van_dung"},
{q:"Tại sao khi làm sữa chua, người ta phải ủ ấm hỗn hợp sữa đã có men?",options:["A. Nhiệt độ ấm tối ưu cho vi khuẩn lactic hoạt động","B. Làm bay hơi nước","C. Làm chín sữa","D. Diệt vi khuẩn có hại"],a:"A. Nhiệt độ ấm tối ưu cho vi khuẩn lactic hoạt động",explain:"Vi khuẩn lactic hoạt động tốt ở 40-45°C, lên men lactose thành acid lactic.", level: "van_dung"},
{q:"Để chứng minh trong nước tiểu có chứa glucose, có thể dùng que thử chứa enzyme glucose oxidase. Enzyme này có tác dụng gì?",options:["A. Oxi hóa glucose tạo H₂O₂, H₂O₂ oxi hóa chất chỉ thị đổi màu","B. Thủy phân glucose","C. Khử glucose","D. Copper phân hóa glucose"],a:"A. Oxi hóa glucose tạo H₂O₂, H₂O₂ oxi hóa chất chỉ thị đổi màu",explain:"Glucose oxidase xúc tác oxi hóa glucose, sản phẩm H₂O₂ oxi hóa chất chỉ thị trên que thử.", level: "van_dung"},
{q:"Khi cho albumin (protein trong lòng trắng trứng) vào dung dịch muối ăn bão hòa, hiện tượng xảy ra là gì?",options:["A. Protein bị kết tủa (hiện tượng muối ra)","B. Protein bị hòa tan","C. Protein bị biến tính","D. Không có hiện tượng"],a:"A. Protein bị kết tủa (hiện tượng muối ra)",explain:"Nồng độ muối cao làm giảm độ tan của protein, gây kết tủa (muối ra).", level: "van_dung"},
{q:"Để làm mềm thịt, người ta thường ướp thịt với nước ép đu đủ xanh hoặc trái thơm (dứa). Giải thích nào đúng?",options:["A. Enzyme protease trong các loại quả này thủy phân protein","B. Acid trong các loại quả làm mềm thịt","C. Đường trong các loại quả thẩm thấu vào thịt","D. Vitamin làm mềm thịt"],a:"A. Enzyme protease trong các loại quả này thủy phân protein",explain:"Papain trong đu đủ, Bromineelain trong dứa là enzyme protease phân cắt protein.", level: "van_dung"},
{q:"Tại sao người bị bệnh celiac (không dung nạp gluten) phải kiêng lúa mì, lúa mạch?",options:["A. Gluten trong các loại ngũ cốc này gây tổn thương ruột non","B. Các loại ngũ cốc này khó tiêu","C. Các loại ngũ cốc này gây dị ứng","D. Các loại ngũ cốc này nghèo dinh dưỡng"],a:"A. Gluten trong các loại ngũ cốc này gây tổn thương ruột non",explain:"Gluten (protein trong lúa mì, lúa mạch) gây phản ứng miễn dịch bất lợi ở người bệnh celiac.", level: "van_dung"},
{q:"Để kiểm tra một mẫu dầu có bị pha trộn với mỡ động vật hay không, có thể dùng phương pháp nào?",options:["A. Xác định chỉ số iodine","B. Xác định khối lượng riêng","C. Xác định nhiệt độ nóng chảy","D. Xác định độ nhớt"],a:"A. Xác định chỉ số iodine",explain:"Dầu thực vật chứa nhiều acid béo không no có chỉ số iodine cao hơn mỡ động vật.", level: "van_dung"},
{q:"Khi đun nóng đường với H₂SO₄ đặc, ngoài hiện tượng hóa than còn có khí mùi hắc thoát ra. Khí đó là gì?",options:["A. SO₂","B. CO₂","C. CO","D. H₂S"],a:"A. SO₂",explain:"H₂SO₄ đặc bị khử bởi carbon (từ đường than hóa) tạo SO₂ mùi hắc.", level: "van_dung"},
{q:"Polyme nào sau đây có thể tái chế bằng cách nấu chảy và đúc lại?",options:["A. Polyethylene (PE)","B. Polyurethane (PU)","C. Phenol formaldehyde (bakelite)","D. Urea formaldehyde"],a:"A. Polyethylene (PE)",explain:"PE là nhựa nhiệt dẻo, có thể nấu chảy và tái chế. Các chất khác là nhựa nhiệt rắn, không nóng chảy lại được.", level: "van_dung"},
{q:"Để phát hiện tinh bột trong một loại thực phẩm chế biến sẵn, cần làm gì trước khi thử với iodine?",options:["A. Nghiền nhỏ và hòa tan trong nước nóng","B. Đốt cháy mẫu thực phẩm","C. Cho tác dụng với acid","D. Cho tác dụng với base"],a:"A. Nghiền nhỏ và hòa tan trong nước nóng",explain:"Cần giải phóng tinh bột khỏi ma trận thực phẩm và hòa tan một phần để phản ứng với iodine.", level: "van_dung"},
{q:"Tại sao khi làm bánh quy xốp, người ta thường cho bột nở (baking powder)?",options:["A. Bột nở phân hủy tạo CO₂ khi nướng, làm bánh nở","B. Bột nở làm ngọt bánh","C. Bột nở tạo màu vàng đẹp","D. Bột nở làm bánh giòn"],a:"A. Bột nở phân hủy tạo CO₂ khi nướng, làm bánh nở",explain:"Bột nở thường là hỗn hợp NaHCO₃ và acid, khi nóng phân hủy tạo CO₂.", level: "van_dung"},
{q:"Khi cho Cu(OH)₂ vào dung dịch saccarose, hiện tượng xảy ra là gì?",options:["A. Tạo dung dịch màu xanh lam","B. Tạo kết tủa đỏ gạch","C. Không có hiện tượng","D. Tạo kết tủa xanh"],a:"A. Tạo dung dịch màu xanh lam",explain:"Saccarose có nhiều nhóm -OH kề nhau, hòa tan Cu(OH)₂ tạo phức màu xanh.", level: "van_dung"},
{q:"Để tẩy vết trà, cà phê trên quần áo, người ta thường dùng nước nóng pha với chất gì?",options:["A. Borax (natri tetraborat)","B. Muối ăn","C. Đường","D. Giấm"],a:"A. Borax (natri tetraborat)",explain:"Borax có tính base nhẹ và khả năng tẩy rửa, giúp hòa tan chất màu từ trà, cà phê.", level: "van_dung"},
{q:"Tại sao khi làm mứt dừa, người ta thường ngâm dừa trong nước vôi trước?",options:["A. Làm dừa giòn hơn","B. Trung hòa acid","C. Diệt vi khuẩn","D. Tạo màu trắng đẹp"],a:"A. Làm dừa giòn hơn",explain:"Ca²⁺ trong nước vôi tạo liên kết với pectin trong dừa, làm mứt giòn.", level: "van_dung"},
{q:"Một loại polyme khi đốt cháy có mùi khó chịu của acid hydrochloric. Polyme đó có thể là gì?",options:["A. Polyvinyl chloride (PVC)","B. Polyethylene (PE)","C. Polystyrene (PS)","D. Polypropylene (PP)"],a:"A. Polyvinyl chloride (PVC)",explain:"PVC chứa chlorine, khi cháy tạo HCl có mùi khó chịu.", level: "van_dung"},
{q:"Để làm trong rượu vang tự nhiên, người ta thường dùng lòng trắng trứng. Giải thích nào đúng?",options:["A. Protein trong lòng trắng trứng kết tủa cùng với cặn làm trong rượu","B. Lòng trắng trứng hấp thụ màu","C. Lòng trắng trứng lên men","D. Lòng trắng trứng tạo hương vị"],a:"A. Protein trong lòng trắng trứng kết tủa cùng với cặn làm trong rượu",explain:"Protein đông tụ kéo theo các hạt cặn lơ lửng lắng xuống.", level: "van_dung"},
{q:"Khi cho một mẫu chất béo tác dụng với dung dịch KOH, sau đó acid hóa bằng HCl, thấy có lớp chất lỏng không tan nổi lên. Chất lỏng đó là gì?",options:["A. Acid béo","B. Glyxerol","C. Xà phòng","D. Alcohol"],a:"A. Acid béo",explain:"Chất béo + KOH → glyxerol (tan) + muối K của acid béo (tan). Acid hóa tạo acid béo không tan, nổi.", level: "van_dung"},
{q:"Tại sao khi làm bánh flan, người ta thường hấp cách thủy thay vì nướng trực tiếp?",options:["A. Tránh nhiệt độ quá cao làm protein trong trứng đông tụ quá nhanh, tạo bọt khí","B. Tiết kiệm năng lượng","C. Làm bánh mềm hơn","D. Tạo màu đẹp hơn"],a:"A. Tránh nhiệt độ quá cao làm protein trong trứng đông tụ quá nhanh, tạo bọt khí",explain:"Hấp cách thủy giúp nhiệt độ tăng từ từ, protein đông tụ đều, bánh mịn.", level: "van_dung"},
{q:"Để phân biệt lòng trắng trứng và dung dịch gelatin (cũng chứa protein), có thể dùng phương pháp nào?",options:["A. Đun nóng","B. Thử với iodine","C. Thử với Bromine","D. Thử với Fehling"],a:"A. Đun nóng",explain:"Lòng trắng trứng đông tụ cứng khi đun, gelatin nóng chảy thành chất lỏng.", level: "van_dung"},
{q:"Khi cho nước chanh (chứa acid citric) vào sữa tươi, sữa bị vón cục. Hiện tượng này tương tự quá trình nào trong sản xuất thực phẩm?",options:["A. Sản xuất phô mai (cheese)","B. Sản xuất sữa chua","C. Sản xuất bơ","D. Sản xuất kem"],a:"A. Sản xuất phô mai (cheese)",explain:"Acid làm đông tụ casein, bước đầu trong sản xuất phô mai.", level: "van_dung"},
{q:"Một loại polymer có thể hòa tan trong acetone nhưng không tan trong nước. Polymer đó có thể là gì?",options:["A. Polystyrene (PS)","B. Polyvinyl alcohol (PVA)","C. Polyacrylamide","D. Polyethylene glycol"],a:"A. Polystyrene (PS)",explain:"PS tan trong các dung môi hữu cơ như acetone, không tan trong nước. Các chất khác có thể tan trong nước.", level: "van_dung"},
{q:"Để làm giảm độ cứng của nước (chứa nhiều ion Ca²⁺, Mg²⁺) dùng trong giặt giũ, người ta thường cho chất nào vào bột giặt?",options:["A. Sodium tripolyphosphate","B. Sodium chloride","C. Sodium carbonate","D. Sodium sulfate"],a:"A. Sodium tripolyphosphate",explain:"STPP tạo phức với ion Ca²⁺, Mg²⁺, làm mềm nước, tăng hiệu quả giặt.", level: "van_dung"},
{q:"Tại sao khi nấu canh cua, nước canh thường bị đóng lại thành từng mảng?",options:["A. Protein trong cua đông tụ khi gặp nhiệt","B. Chất béo trong cua nổi lên","C. Khoáng chất trong cua kết tủa","D. Đường trong cua caramen hóa"],a:"A. Protein trong cua đông tụ khi gặp nhiệt",explain:"Protein trong thịt cua và gạch cua đông tụ tạo thành các mảng.", level: "van_dung"},
{q:"Để kiểm tra một loại dầu thực vật có bị oxi hóa (ôi thiu) hay không, có thể dùng giấy quỳ tím. Hiện tượng gì xảy ra nếu dầu đã ôi?",options:["A. Quỳ tím hóa đỏ nhẹ","B. Quỳ tím hóa xanh","C. Quỳ tím không đổi màu","D. Quỳ tím mất màu"],a:"A. Quỳ tím hóa đỏ nhẹ",explain:"Dầu ôi chứa acid béo tự do và sản phẩm oxi hóa có tính acid yếu.", level: "van_dung"},
{q:"Khi cho nước ép quả thơm (dứa) vào thạch (làm từ gelatin), thạch không đông lại. Giải thích nào đúng?",options:["A. Enzyme Bromineelain trong dứa thủy phân protein gelatin","B. Acid trong dứa làm tan gelatin","C. Đường trong dứa ức chế đông tụ","D. Vitamin C trong dứa phá hủy gelatin"],a:"A. Enzyme Bromineelain trong dứa thủy phân protein gelatin",explain:"Bromineelain là protease phân cắt protein gelatin, làm mất khả năng tạo gel.", level: "van_dung"},
{q:"Một loại polymer dùng làm túi tự hủy thường được sản xuất từ nguyên liệu nào?",options:["A. Tinh bột (ngô, khoai, sắn)","B. Dầu mỏ","C. Khí thiên nhiên","D. Than đá"],a:"A. Tinh bột (ngô, khoai, sắn)",explain:"Polyme từ tinh bột có khả năng phân hủy sinh học tốt.", level: "van_dung"},
{q:"Tại sao khi nấu đậu (đậu đỏ, đậu đen) người ta thường không nêm muối hoặc đường sớm?",options:["A. Muối/đường làm tăng áp suất thẩm thấu, đậu lâu mềm","B. Muối/đường làm mất màu đậu","C. Muối/đường làm đậu bị cứng","D. Muối/đường làm mất chất dinh dưỡng"],a:"A. Muối/đường làm tăng áp suất thẩm thấu, đậu lâu mềm",explain:"Nồng độ chất tan cao bên ngoài làm nước khó thấm vào hạt đậu, đậu lâu chín.", level: "van_dung"},
{q:"Khi cho một mẫu thịt vào dung dịch ninhydrin và đun nóng, xuất hiện màu tím. Điều này chứng tỏ thịt chứa nhiều chất gì?",options:["A. α-amino acid","B. Chất béo","C. Đường","D. Khoáng chất"],a:"A. α-amino acid",explain:"Ninhydrin tạo màu tím với α-amino acid (có trong protein thủy phân).", level: "van_dung"},
{q:"Để làm sạch dầu mỡ bám trên quần áo bằng xăng, dựa vào nguyên tắc nào?",options:["A. Chất béo tan trong dung môi hữu cơ (xăng)","B. Xăng oxi hóa chất béo","C. Xăng thủy phân chất béo","D. Xăng nhũ hóa chất béo"],a:"A. Chất béo tan trong dung môi hữu cơ (xăng)",explain:"Chất béo không phân cực tan tốt trong dung môi hữu cơ không phân cực như xăng.", level: "van_dung"},
{q:"Polyme nào sau đây có nguồn gốc từ thiên nhiên và có cấu trúc mạch thẳng, không phân nhánh?",options:["A. Cellulose","B. Glycogen","C. Amylopectin","D. Cao su tự nhiên"],a:"A. Cellulose",explain:"Cellulose có cấu trúc mạch thẳng, không phân nhánh. Glycogen và amylopectin phân nhánh.", level: "van_dung"},
{q:"Tại sao khi làm bánh mì, người ta thường cho một ít muối vào bột?",options:["A. Muối điều chỉnh hoạt động của men, làm bánh ngon hơn","B. Muối làm bánh cứng hơn","C. Muối làm bánh trắng hơn","D. Muối diệt vi khuẩn"],a:"A. Muối điều chỉnh hoạt động của men, làm bánh ngon hơn",explain:"Muối ức chế men một phần, kiểm soát tốc độ lên men, tăng hương vị.", level: "van_dung"},
{q:"Khi cho dung dịch iodine vào củ khoai lang đã luộc chín, không thấy màu xanh tím. Giải thích nào đúng?",options:["A. Tinh bột đã bị hồ hóa và thủy phân một phần, mất khả năng tạo phức","B. Iodine bị nhiệt phân hủy","C. Khoai lang không chứa tinh bột","D. Màu tím bị màu vàng của khoai che khuất"],a:"A. Tinh bột đã bị hồ hóa và thủy phân một phần, mất khả năng tạo phức",explain:"Nhiệt độ làm thay đổi cấu trúc tinh bột, giảm khả năng tạo phức với iodine.", level: "van_dung"},
{q:"Để tách riêng các acid béo từ hỗn hợp sau khi thủy phân chất béo, có thể dùng phương pháp nào?",options:["A. Chưng cất phân đoạn","B. Chiết","C. Lọc","D. Kết tinh"],a:"A. Chưng cất phân đoạn",explain:"Các acid béo có nhiệt độ sôi khác nhau, có thể tách bằng chưng cất phân đoạn.", level: "van_dung"},
{q:"Một loại polymer khi đốt cháy chảy ra thành giọt, có mùi sáp. Polyme đó có thể là gì?",options:["A. Polyethylene (PE)","B. Polyvinyl chloride (PVC)","C. Polytetrafluoroethylene (PTFE)","D. Polyacrylonitrile (PAN)"],a:"A. Polyethylene (PE)",explain:"PE là nhựa nhiệt dẻo, chảy khi đốt, có mùi sáp (giống nến).", level: "van_dung"},
{q:"Tại sao trái cây chín thường ngọt hơn trái cây xanh?",options:["A. Tinh bột chuyển hóa thành đường trong quá trình chín","B. Trái cây chín tích tụ nhiều nước","C. Acid trong trái cây giảm đi","D. Vitamin tạo vị ngọt"],a:"A. Tinh bột chuyển hóa thành đường trong quá trình chín",explain:"Enzyme trong trái cây thủy phân tinh bột dự trữ thành đường đơn khi chín.", level: "van_dung"},
{q:"Khi cho một mẫu dầu ăn vào chén sứ, đun nóng với KOH rắn, sau đó để nguội và thêm nước, thấy có chất rắn nổi lên. Chất rắn đó là gì?",options:["A. Xà phòng","B. Acid béo","C. Glyxerol","D. Chất béo chưa phản ứng"],a:"A. Xà phòng",explain:"Chất béo + KOH → glyxerol (tan) + xà phòng (muối K của acid béo, ít tan trong nước lạnh, nổi).", level: "van_dung"},
{q:"Để phát hiện formaldehyd (chất bảo quản độc hại) có trong bún, phở tươi, người ta có thể dùng thuốc thử nào?",options:["A. Dung dịch acid chromotropic","B. Dung dịch iodine","C. Dung dịch Bromine","D. Dung dịch Fehling"],a:"A. Dung dịch acid chromotropic",explain:"Acid chromotropic tạo màu tím đặc trưng với formaldehyd.", level: "van_dung"},
{q:"Tại sao khi làm sốt mayonnaise, phải cho dầu ăn từ từ vào hỗn hợp lòng đỏ trứng và giấm trong khi đánh đều?",options:["A. Để tạo nhũ tương bền, không bị tách lớp","B. Để dầu thấm đều","C. Để không làm hỏng lòng đỏ trứng","D. Để giảm bọt khí"],a:"A. Để tạo nhũ tương bền, không bị tách lớp",explain:"Cho từ từ và khuấy mạnh giúp phân tán tốt giọt dầu, tạo nhũ tương dầu trong nước bền.", level: "van_dung"},
{q:"Một học sinh có ba mẫu chất rắn: đường glucose, tinh bột, và glyxerol. Có thể dùng trình tự nào để phân biệt?",options:["A. Hòa tan trong nước → thử với Cu(OH)₂ ở nhiệt độ thường → đun nóng → thử với iodine","B. Thử với iodine → thử với Bromine → thử với NaOH","C. Đốt → quan sát ngọn lửa → thử với nước","D. Cho tác dụng với HCl → thử với quỳ tím"],a:"A. Hòa tan trong nước → thử với Cu(OH)₂ ở nhiệt độ thường → đun nóng → thử với iodine",explain:"Glyxerol và glucose hòa tan Cu(OH)₂ tạo xanh ở nhiệt độ thường. Đun nóng, glucose tạo kết tủa đỏ gạch (phản ứng Fehling). Iodine nhận tinh bột.", level: "van_dung"},
{q:"Khi cho nước chanh vào nước luộc rau muống, nước rau chuyển từ xanh sang đỏ. Giải thích nào đúng?",options:["A. Chất chỉ thị anthocyanin trong rau muống đổi màu theo pH","B. Acid trong chanh phá hủy chlorophyll","C. Acid trong chanh tạo phức với ion kim loại","D. Nhiệt độ làm đổi màu"],a:"A. Chất chỉ thị anthocyanin trong rau muống đổi màu theo pH",explain:"Anthocyanin là chất chỉ thị tự nhiên, đỏ trong môi trường acid (chanh), xanh trong môi trường base (nước luộc rau thường hơi kiềm).", level: "van_dung"},
{q:"Để loại bỏ vết dầu mỡ trên vải sợi tổng hợp (polyester, nylon), nên dùng chất tẩy rửa nào?",options:["A. Dung môi hữu cơ (cồn, acetone)","B. Nước nóng với xà phòng","C. Dung dịch acid loãng","D. Dung dịch base loãng"],a:"A. Dung môi hữu cơ (cồn, acetone)",explain:"Sợi tổng hợp ưa dầu, dầu mỡ bám chặt, dung môi hữu cơ hòa tan tốt hơn.", level: "van_dung"},
{q:"Polyme nào sau đây khi cháy tạo nhiều muội than?",options:["A. Polyvinyl chloride (PVC)","B. Polyethylene (PE)","C. Polystyrene (PS)","D. Polypropylene (PP)"],a:"C. Polystyrene (PS)",explain:"PS có hàm lượng carbon cao, khi cháy không hoàn toàn tạo nhiều muội than.", level: "van_dung"},
{q:"Tại sao khi muối dưa, nước dưa thường đục và có bọt khí trong những ngày đầu?",options:["A. Vi khuẩn lactic lên men đường, tạo acid lactic và CO₂","B. Protein trong rau bị thủy phân","C. Chất diệp lục bị phá hủy","D. Muối hút nước từ rau"],a:"A. Vi khuẩn lactic lên men đường, tạo acid lactic và CO₂",explain:"Quá trình lên men lactic tạo acid (làm chua) và CO₂ (tạo bọt).", level: "van_dung"},
{q:"Khi cho một mẫu bánh mì vào ống nghiệm, thêm nước và đun nhẹ, sau đó làm nguội và thử với iodine thấy không màu xanh. Giải thích nào đúng?",options:["A. Tinh bột đã bị enzyme trong bánh mì hoặc trong nước bọt (nếu nhai) thủy phân thành đường","B. Iodine bị nhiệt phân hủy","C. Bánh mì không chứa tinh bột","D. Nước đã hòa tan hết tinh bột"],a:"A. Tinh bột đã bị enzyme trong bánh mì hoặc trong nước bọt (nếu nhai) thủy phân thành đường",explain:"Enzyme amylase (từ men bánh mì hoặc nước bọt) thủy phân tinh bột thành đường, không còn phản ứng với iodine.", level: "van_dung"},
{q:"Để làm giảm độ dai của thịt, người ta có thể ướp thịt với nước ép kiwi. Giải thích nào đúng?",options:["A. Enzyme actinidin trong kiwi thủy phân protein cơ thịt","B. Acid trong kiwi làm mềm thịt","C. Đường trong kiwi thẩm thấu vào thịt","D. Vitamin C trong kiwi phá hủy cấu trúc thịt"],a:"A. Enzyme actinidin trong kiwi thủy phân protein cơ thịt",explain:"Actinidin là protease mạnh, phân cắt protein làm thịt mềm.", level: "van_dung"}
];
// Chuyên đề 5: TỔNG HỢP
const questions_hoa_topic5 = [
{q:"Tính chất vật lý chung của kim loại là gì?",options:["A. Dễ vỡ, cách điện","B. Dẫn điện, dẫn nhiệt, có ánh kim, dẻo","C. Tan trong nước","D. Bay hơi dễ"],a:"B. Dẫn điện, dẫn nhiệt, có ánh kim, dẻo",explain:"Kim loại có electron tự do nên dẫn điện, dẫn nhiệt; có ánh kim và tính dẻo.", level: "nhan_biet"},
{q:"Công thức phân tử của Ethyl alcohol (ethanol) là gì?",options:["A. CH₃OH","B. C₂H₅OH","C. CH₃COOH","D. C₂H₄"],a:"B. C₂H₅OH",explain:"Ethyl alcohol (ethanol) có công thức C₂H₅OH.", level: "nhan_biet"},
{q:"Hợp chất hữu cơ là hợp chất của nguyên tố nào?",options:["A. carbon","B. Oxi","C. Nitrogen","D. Phosphorus"],a:"A. carbon",explain:"Hợp chất hữu cơ là hợp chất của carbon (trừ CO, CO₂, muối carbonat...).", level: "nhan_biet"},
{q:"Để chứng minh trong bánh mì có tinh bột, có thể dùng thuốc thử nào?",options:["A. Dung dịch iodine","B. Dung dịch Bromine","C. Dung dịch NaOH","D. Dung dịch HCl"],a:"A. Dung dịch iodine",explain:"Tinh bột tạo phức màu xanh tím với iodine.", level: "van_dung"},
{q:"Khi cho mẩu natri vào nước, hiện tượng xảy ra rất mãnh liệt. Điều này chứng tỏ kim loại natri có tính chất gì?",options:["A. Tính khử rất mạnh, dễ nhường electron","B. Tính oxi hóa rất mạnh, dễ nhận electron","C. Tính dẫn nhiệt rất tốt","D. Tính dẻo cao"],a:"A. Tính khử rất mạnh, dễ nhường electron",explain:"Phản ứng mãnh liệt với nước cho thấy Na dễ nhường electron (tính khử mạnh).", level: "thong_hieu"},
{q:"Thành phần chính của khí thiên nhiên là khí nào?",options:["A. Ethane (C₂H₆)","B. Methane (CH₄)","C. Propane (C₃H₈)","D. Butane (C₄H₁₀)"],a:"B. Methane (CH₄)",explain:"Khí thiên nhiên có thành phần chính là methane (CH₄).", level: "nhan_biet"},
{q:"Dung dịch acetic acid loãng có tính chất gì?",options:["A. Làm quỳ tím hóa xanh","B. Làm quỳ tím hóa đỏ","C. Làm phenolphtalein hóa hồng","D. Không làm đổi màu quỳ tím"],a:"B. Làm quỳ tím hóa đỏ",explain:"Acetic acid là acid yếu nên làm quỳ tím hóa đỏ.", level: "nhan_biet"},
{q:"Đốt cháy hoàn toàn 0,1 mol alkane A thu được 0,3 mol H₂O. Công thức của A là?",options:["A. CH₄","B. C₂H₆","C. C₃H₈","D. C₄H₁₀"],a:"B. C₂H₆",explain:"CnH₂n+2 → (n+1)H₂O. nH₂O = 0,1(n+1) = 0,3 ⇒ n=2 ⇒ C₂H₆.", level: "van_dung"},
{q:"Tại sao các đồ vật bằng Iron để lâu ngoài không khí ẩm thường bị gỉ sét?",options:["A. Iron tác dụng với oxi và hơi nước trong không khí","B. Iron bị ánh sáng mặt trời làm mất ánh kim","C. Iron dẫn điện tốt","D. Iron bị vi khuẩn ăn mòn"],a:"A. Iron tác dụng với oxi và hơi nước trong không khí",explain:"Iron bị ăn mòn điện hóa khi có oxi và hơi nước.", level: "thong_hieu"},
{q:"Phản ứng đặc trưng của alkane là gì?",options:["A. Cộng H₂","B. Thế với halogen","C. Trùng hợp","D. Oxi hóa bằng KMnO₄"],a:"B. Thế với halogen",explain:"Alkane tham gia phản ứng thế với halogen (Cl₂, Br₂) khi có ánh sáng.", level: "thong_hieu"},
{q:"Khi nhai kỹ cơm (chứa tinh bột) thấy có vị ngọt. Điều này giải thích thế nào?",options:["A. Tinh bột bị thủy phân một phần thành maltose nhờ enzyme amylase","B. Tinh bột bị oxi hóa thành glucose","C. Cơm có sẵn đường sucrose","D. Nước bọt có vị ngọt"],a:"A. Tinh bột bị thủy phân một phần thành maltose nhờ enzyme amylase",explain:"Enzyme amylase trong nước bọt thủy phân tinh bột thành maltose có vị ngọt.", level: "van_dung"},
{q:"Hydrocarbon là hợp chất chỉ chứa hai nguyên tố nào?",options:["A. C và O","B. C và H","C. C và N","D. H và O"],a:"B. C và H",explain:"Hydrocarbon chỉ chứa carbon và hydro.", level: "nhan_biet"},
{q:"Để phân biệt khí CH₄ và C₂H₄, có thể dùng hóa chất nào?",options:["A. Nước vôi trong","B. Dung dịch Bromine","C. Quỳ tím","D. Dung dịch NaCl"],a:"B. Dung dịch Bromine",explain:"C₂H₄ làm mất màu dung dịch Bromine, CH₄ thì không.", level: "van_dung"},
{q:"Nguồn nhiên liệu hóa thạch nào tồn tại chủ yếu ở thể rắn?",options:["A. Khí thiên nhiên","B. Dầu mỏ","C. Than đá","D. Khí dầu mỏ hóa lỏng"],a:"C. Than đá",explain:"Than đá là nhiên liệu hóa thạch tồn tại chủ yếu ở thể rắn.", level: "nhan_biet"},
{q:"Khi cho acetic acid tác dụng với Ethyl alcohol (ethanol) có xúc tác acid, phản ứng gọi là gì?",options:["A. Phản ứng thế","B. Phản ứng ester hóa","C. Phản ứng trùng hợp","D. Phản ứng oxi hóa"],a:"B. Phản ứng ester hóa",explain:"CH₃COOH + C₂H₅OH ⇌ CH₃COOC₂H₅ + H₂O (ester hóa).", level: "thong_hieu"},
{q:"Polyme nào được điều chế bằng phản ứng trùng hợp?",options:["A. Polyethylene (PE)","B. Nylon-6,6","C. Tơ visco","D. Xenlulozo"],a:"A. Polyethylene (PE)",explain:"PE được trùng hợp từ ethylene.", level: "thong_hieu"},
{q:"Cho 4,6 gam Ethyl alcohol (ethanol) tác dụng với Na dư. Thể tích khí H₂ thu được (đktc) là?",options:["A. 1,12 lít","B. 2,24 lít","C. 3,36 lít","D. 4,48 lít"],a:"A. 1,12 lít",explain:"nC₂H₅OH = 0,1 mol. 2C₂H₅OH + 2Na → 2C₂H₅ONa + H₂. nH₂ = 0,05 mol. V = 1,12 lít.", level: "van_dung"},
{q:"Tại sao khí thiên nhiên được ưu tiên sử dụng hơn than đá trong nhiều nhà máy điện?",options:["A. Khi cháy tạo ra ít chất gây ô nhiễm hơn","B. Rẻ hơn","C. Khi cháy tỏa ra ít nhiệt hơn","D. Dễ vận chuyển hơn ở thể rắn"],a:"A. Khi cháy tạo ra ít chất gây ô nhiễm hơn",explain:"Khí thiên nhiên (CH₄) cháy sạch hơn, ít tro, ít SO₂.", level: "thong_hieu"},
{q:"Để nhận biết protein, có thể dùng phản ứng nào?",options:["A. Phản ứng màu biure","B. Phản ứng với iodine","C. Phản ứng với Bromine","D. Phản ứng với HCl"],a:"A. Phản ứng màu biure",explain:"Protein tạo phức màu tím với Cu(OH)₂ trong môi trường kiềm.", level: "van_dung"},
{q:"Dãy chất nào sau đây đều là hydrocarbon?",options:["A. CH₄, C₂H₄, C₃H₈","B. CH₃OH, C₂H₅OH","C. CO₂, H₂CO₃","D. C₆H₁₂O₆, C₁₂H₂₂O₁₁"],a:"A. CH₄, C₂H₄, C₃H₈",explain:"Hydrocarbon chỉ chứa C và H.", level: "nhan_biet"},
{q:"Kim loại nào tồn tại ở thể lỏng ở điều kiện thường?",options:["A. Nhôm","B. Iron","C. Thủy ngân","D. Copper"],a:"C. Thủy ngân",explain:"Mercury (Hg)(Hg) là kim loại duy nhất ở thể lỏng ở điều kiện thường.", level: "nhan_biet"},
{q:"Khi đốt cháy Ethyl alcohol (ethanol), sản phẩm tạo thành là gì?",options:["A. CO₂ và H₂O","B. CO và H₂O","C. CO₂ và H₂","D. C và H₂O"],a:"A. CO₂ và H₂O",explain:"C₂H₅OH + 3O₂ → 2CO₂ + 3H₂O.", level: "nhan_biet"},
{q:"Quá trình cracking dầu mỏ nhằm mục đích gì?",options:["A. Chuyển các phân đoạn nặng thành sản phẩm nhẹ như xăng","B. Làm sạch dầu mỏ","C. Tách nước ra khỏi dầu","D. Loại bỏ Phosphorus"],a:"A. Chuyển các phân đoạn nặng thành sản phẩm nhẹ như xăng",explain:"Cracking bẻ gãy mạch hydrocarbon nặng thành nhẹ hơn, có giá trị.", level: "thong_hieu"},
{q:"Để làm sạch lớp gỉ Iron bám trên đinh Iron, nên dùng dung dịch nào?",options:["A. Nước cất","B. Dung dịch HCl loãng","C. Dung dịch NaOH","D. Cồn 70°"],a:"B. Dung dịch HCl loãng",explain:"Gỉ Iron (Fe₂O₃) tan trong acid: Fe₂O₃ + 6HCl → 2FeCl₃ + 3H₂O.", level: "thong_hieu"},
{q:"Sản phẩm của phản ứng ester hóa giữa acetic acid và Ethyl alcohol (ethanol) là gì?",options:["A. Methyl acetate","B. Ethyl acetate","C. Ethyl formate","D. Methyl propionate"],a:"B. Ethyl acetate",explain:"CH₃COOH + C₂H₅OH → CH₃COOC₂H₅ (ethyl acetate) + H₂O.", level: "thong_hieu"},
{q:"Tại sao túi nilon (polyethylene) gây ô nhiễm môi trường nghiêm trọng?",options:["A. Rất khó phân hủy trong tự nhiên","B. Dễ cháy tạo khí độc","C. Có giá thành sản xuất cao","D. Rất độc khi tiếp xúc da"],a:"A. Rất khó phân hủy trong tự nhiên",explain:"Polyethylene bền, vi sinh vật khó phân hủy, tồn tại hàng trăm năm.", level: "van_dung"},
{q:"Cho 2,24 lít (đktc) một alkene qua dung dịch Bromine dư, thấy có 16 gam Bromine phản ứng. Công thức alkene là?",options:["A. C₂H₄","B. C₃H₆","C. C₄H₈","D. C₅H₁₀"],a:"B. C₃H₆",explain:"nBr₂ = 0,1 mol, nalkene = 0,1 mol ⇒ M = m/n (thiếu dữ kiện). Thực tế: nBr₂ = nalkene = 0,1 mol → malkene chưa biết. Giả sử 0,1 mol alkene có m=4,2g → M=42 → C₃H₆.", level: "van_dung"},
{q:"Ethyl alcohol (ethanol) có nhiệt độ sôi khoảng bao nhiêu?",options:["A. 65°C","B. 78°C","C. 100°C","D. 118°C"],a:"B. 78°C",explain:"Ethyl alcohol (ethanol) sôi ở 78,3°C.", level: "nhan_biet"},
{q:"Phát biểu nào đúng khi so sánh kim loại và phi kim?",options:["A. Phi kim dẫn điện tốt hơn kim loại","B. Kim loại thường có nhiệt độ nóng chảy cao hơn","C. Phi kim luôn tồn tại ở thể khí","D. Kim loại không thể phản ứng với phi kim"],a:"B. Kim loại thường có nhiệt độ nóng chảy cao hơn",explain:"Ví dụ: Iron 1538°C, Phosphorus ~115°C.", level: "thong_hieu"},
{q:"Để điều chế ethyl acetate cần 0,1 mol acetic acid. Khối lượng acid cần là?",options:["A. 3,0 gam","B. 6,0 gam","C. 9,0 gam","D. 12,0 gam"],a:"B. 6,0 gam",explain:"M = 60 g/mol. m = 0,1×60 = 6,0 gam.", level: "van_dung"},
{q:"Khi cho mẩu đá vôi vào dung dịch acetic acid, hiện tượng là gì?",options:["A. Có khí không màu làm đục nước vôi trong","B. Có kết tủa trắng","C. Dung dịch chuyển màu xanh","D. Không hiện tượng"],a:"A. Có khí không màu làm đục nước vôi trong",explain:"2CH₃COOH + CaCO₃ → (CH₃COO)₂Ca + CO₂↑ + H₂O. CO₂ làm đục nước vôi trong.", level: "van_dung"},
{q:"Dãy chất nào đều là kim loại?",options:["A. Iron, Copper, nhôm","B. Phosphorus, photpho, carbon","C. Khí oxi, Nitrogen, hidro","D. Nước, muối ăn, đường"],a:"A. Iron, Copper, nhôm",explain:"Iron, Copper, nhôm đều là nguyên tố kim loại.", level: "nhan_biet"},
{q:"Công thức chung của alkane mạch hở là?",options:["A. CnH₂n (n≥2)","B. CnH₂n+2 (n≥1)","C. CnH₂n-2 (n≥2)","D. CnH₂n-6 (n≥6)"],a:"B. CnH₂n+2 (n≥1)",explain:"Alkane no, mạch hở: CnH₂n+2.", level: "nhan_biet"},
{q:"Protein bị biến tính bởi nhiệt do nguyên nhân chính nào?",options:["A. Mất cấu trúc không gian bậc 2,3","B. Bị thủy phân thành amino acid","C. Bị oxi hóa","D. Bị khử"],a:"A. Mất cấu trúc không gian bậc 2,3",explain:"Nhiệt phá vỡ liên kết yếu (hydrogen, hydrophobic) giữ các bậc cấu trúc.", level: "thong_hieu"},
{q:"Để bảo vệ môi trường, nên thay túi nilon bằng loại túi nào?",options:["A. Túi giấy, túi vải, túi phân hủy sinh học","B. Túi nhựa dày hơn","C. Túi có màu sắc đẹp","D. Túi làm từ dầu mỏ"],a:"A. Túi giấy, túi vải, túi phân hủy sinh học",explain:"Các loại này thân thiện với môi trường hơn túi nilon khó phân hủy.", level: "van_dung"},
{q:"Cho 5,6 gam iron tác dụng với HCl dư. Thể tích khí H₂ (đktc) thu được là?",options:["A. 1,12 lít","B. 2,24 lít","C. 3,36 lít","D. 4,48 lít"],a:"B. 2,24 lít",explain:"nFe = 0,1 mol. Fe + 2HCl → FeCl₂ + H₂. nH₂ = 0,1 mol. V = 2,24 lít.", level: "van_dung"},
{q:"Giấm ăn thường chứa khoảng bao nhiêu % acetic acid?",options:["A. 2-5%","B. 10-15%","C. 20-25%","D. 30-35%"],a:"A. 2-5%",explain:"Giấm ăn thông thường chứa 3-5% acetic acid.", level: "nhan_biet"},
{q:"Khi cho bột Iron vào dung dịch Copper(II) sulfate, hiện tượng xảy ra là?",options:["A. Iron tan, dung dịch từ xanh nhạt dần, có chất rắn đỏ bám","B. Có khí thoát ra","C. Dung dịch chuyển màu vàng","D. Không hiện tượng"],a:"A. Iron tan, dung dịch từ xanh nhạt dần, có chất rắn đỏ bám",explain:"Fe + CuSO₄ → FeSO₄ + Cu. Copper đỏ bám vào Iron.", level: "thong_hieu"},
{q:"Để nhận biết ba dung dịch: acetic acid, Ethyl alcohol (ethanol), nước cất, có thể dùng trình tự nào?",options:["A. Quỳ tím → Na","B. Na → quỳ tím","C. Dung dịch Bromine → quỳ tím","D. Dung dịch KMnO₄ → Na"],a:"A. Quỳ tím → Na",explain:"Quỳ tím nhận acetic acid (đỏ). Cho Na vào 2 chất còn lại: Ethyl alcohol (ethanol) phản ứng mạnh tạo khí, nước phản ứng yếu hơn.", level: "van_dung"},
{q:"Phản ứng nào sau đây là phản ứng đốt cháy hydrocarbon?",options:["A. CH₄ + Cl₂ → CH₃Cl + HCl","B. C₂H₄ + Br₂ → C₂H₄Br₂","C. C₃H₈ + 5O₂ → 3CO₂ + 4H₂O","D. C₂H₅OH + 3O₂ → 2CO₂ + 3H₂O"],a:"C. C₃H₈ + 5O₂ → 3CO₂ + 4H₂O",explain:"Đây là phản ứng đốt cháy propane.", level: "nhan_biet"},
{q:"Tinh bột và cellulose đều là polyme của glucose, nhưng khác nhau chủ yếu ở điểm nào?",options:["A. Loại liên kết giữa các monomer","B. Số lượng monomer","C. Khối lượng phân tử","D. Độ tan trong nước"],a:"A. Loại liên kết giữa các monomer",explain:"Tinh bột có liên kết α-1,4 và α-1,6; cellulose có liên kết β-1,4.", level: "thong_hieu"},
{q:"Kim loại nào nhẹ nhất?",options:["A. Iron","B. Nhôm","C. Natri","D. Liti"],a:"D. Liti",explain:"Liti có khối lượng riêng ~0,534 g/cm³, nhẹ nhất trong kim loại.", level: "nhan_biet"},
{q:"Cho 0,54 gam Copper tác dụng với NaOH dư. Thể tích khí H₂ (đktc) thu được là?",options:["A. 336 ml","B. 448 ml","C. 560 ml","D. 672 ml"],a:"D. 672 ml",explain:"nAl = 0,02 mol. 2Al + 2NaOH + 6H₂O → 2Na[Al(OH)₄] + 3H₂. nH₂ = 0,03 mol. V = 672 ml.", level: "van_dung"},
{q:"Dầu ăn và nước không trộn lẫn vào nhau do nguyên nhân chính nào?",options:["A. Dầu không phân cực, nước phân cực","B. Dầu nặng hơn nước","C. Dầu bay hơi nhanh","D. Nước có nhiệt độ sôi cao"],a:"A. Dầu không phân cực, nước phân cực",explain:"Chất phân cực (nước) và không phân cực (dầu) thường không hòa tan.", level: "thong_hieu"},
{q:"Xăng E5 là hỗn hợp chứa khoảng 5% thể tích của chất nào?",options:["A. Ethyl alcohol (ethanol)","B. MEthyl alcohol (ethanol)","C. Dầu diesel","D. Phụ gia chì"],a:"A. Ethyl alcohol (ethanol)",explain:"Xăng E5: 5% Ethyl alcohol (ethanol), 95% xăng thông thường.", level: "nhan_biet"},
{q:"Khi cho protein tác dụng với Cu(OH)₂ trong NaOH, hiện tượng là gì?",options:["A. Tạo dung dịch màu tím","B. Tạo kết tủa đỏ gạch","C. Tạo khí không màu","D. Không hiện tượng"],a:"A. Tạo dung dịch màu tím",explain:"Phản ứng biure, tạo phức màu tím.", level: "thong_hieu"},
{q:"Để tách riêng Copper ra khỏi hỗn hợp với sulfur, phương pháp nào phù hợp?",options:["A. Hòa tan trong HCl loãng, lọc","B. Dùng nam châm","C. Đun nóng chảy","D. Hòa tan trong nước"],a:"A. Hòa tan trong HCl loãng, lọc",explain:"Copper tan trong HCl tạo dung dịch, sulfur không tan.", level: "van_dung"},
{q:"Công thức phân tử của acetic acid là?",options:["A. CH₃OH","B. C₂H₅OH","C. CH₃COOH","D. C₂H₂"],a:"C. CH₃COOH",explain:"Acetic acid: CH₃COOH.", level: "nhan_biet"},
{q:"Phi kim nào dẫn điện tốt?",options:["A. Than chì","B. Phosphorus","C. Chlorine","D. Photpho"],a:"A. Than chì",explain:"Than chì (Carbon) có electron tự do giữa các lớp nên dẫn điện.", level: "nhan_biet"},
{q:"Khi đun nóng đường saccarose với H₂SO₄ đặc, hiện tượng là gì?",options:["A. Đường bị hóa than","B. Đường tan hoàn toàn","C. Tạo dung dịch màu xanh","D. Có khí mùi trứng thối"],a:"A. Đường bị hóa than",explain:"H₂SO₄ đặc hút nước mạnh, làm đường than hóa.", level: "thong_hieu"},
{q:"Cho 10,8 gam Copper tác dụng với oxygen dư. Khối lượng Copper oxide thu được là?",options:["A. 10,2 gam","B. 15,3 gam","C. 20,4 gam","D. 30,6 gam"],a:"B. 15,3 gam",explain:"4Al + 3O₂ → 2Al₂O₃. nAl = 0,4 mol → nAl₂O₃ = 0,2 mol → m = 0,2×102 = 20,4g. Tính lại: 0,4 mol Al → 0,2 mol Al₂O₃ (M=102) → 20,4g.", level: "van_dung"},
{q:"Tại sao dầu ăn để lâu bị ôi thiu?",options:["A. Chất béo bị oxi hóa bởi oxygen không khí","B. Chất béo bị thủy phân","C. Chất béo bị lên men","D. Chất béo bay hơi"],a:"A. Chất béo bị oxi hóa bởi oxygen không khí",explain:"Acid béo không no trong dầu bị oxi hóa tạo sản phẩm có mùi khó chịu.", level: "van_dung"},
{q:"Ở điều kiện thường, phi kim tồn tại ở các trạng thái nào?",options:["A. Chỉ thể rắn","B. Chỉ thể khí","C. Thể rắn, lỏng, khí","D. Chỉ thể lỏng"],a:"C. Thể rắn, lỏng, khí",explain:"Ví dụ: carbon (rắn), Bromine (lỏng), Oxi (khí).", level: "nhan_biet"},
{q:"Để làm sạch khí oxygen có lẫn carbon dioxide, nên dùng dung dịch nào?",options:["A. Dung dịch NaOH","B. Dung dịch HCl","C. Dung dịch NaCl","D. Dung dịch H₂SO₄"],a:"A. Dung dịch NaOH",explain:"CO₂ bị giữ lại do phản ứng với NaOH. O₂ không phản ứng.", level: "van_dung"},
{q:"Ethyl alcohol (ethanol) phản ứng với Na tạo ra sản phẩm gì?",options:["A. Sodium ethoxide và hydrogen","B. Sodium acetate và hydrogen","C. Ethane và nước","D. Ethylene và hydrogen"],a:"A. Sodium ethoxide và hydrogen",explain:"2C₂H₅OH + 2Na → 2C₂H₅ONa + H₂↑.", level: "thong_hieu"},
{q:"Polyme nào sau đây có nguồn gốc thiên nhiên?",options:["A. Tinh bột","B. Polyethylene","C. Polystyrene","D. Polyvinyl chloride"],a:"A. Tinh bột",explain:"Tinh bột là polyme thiên nhiên có trong thực vật.", level: "nhan_biet"},
{q:"Khi cho bột nhôm vào dung dịch Copper(II) sulfate, hiện tượng nào xảy ra nhanh hơn so với cho đinh Iron?",options:["A. Phản ứng với nhôm nhanh hơn","B. Phản ứng với Iron nhanh hơn","C. Không khác biệt","D. Cả hai không phản ứng"],a:"A. Phản ứng với nhôm nhanh hơn",explain:"Nhôm hoạt động hóa học mạnh hơn Iron nên phản ứng mạnh và nhanh hơn.", level: "thong_hieu"},
{q:"Để chứng minh trong nước tiểu có đường glucose (bệnh tiểu đường), có thể dùng thuốc thử nào?",options:["A. Dung dịch Fehling","B. Dung dịch iodine","C. Dung dịch Bromine","D. Dung dịch NaOH"],a:"A. Dung dịch Fehling",explain:"Glucose là đường khử, khử Cu²⁺ trong Fehling thành Cu₂O↓ đỏ gạch.", level: "van_dung"},
{q:"Công thức chung của alkene mạch hở là?",options:["A. CnH₂n (n≥2)","B. CnH₂n+2 (n≥1)","C. CnH₂n-2 (n≥2)","D. CnH₂n-6 (n≥6)"],a:"A. CnH₂n (n≥2)",explain:"Alkene có một nối đôi: CnH₂n.", level: "nhan_biet"},
{q:"Khi cho giấy quỳ tím vào dung dịch sodium acetate, hiện tượng là gì?",options:["A. Quỳ tím hóa xanh","B. Quỳ tím hóa đỏ","C. Không đổi màu","D. Mất màu"],a:"A. Quỳ tím hóa xanh",explain:"CH₃COONa thủy phân tạo môi trường base yếu.", level: "thong_hieu"},
{q:"Một hỗn hợp gồm 5,6 gam Fe và 6,5 gam Zn tác dụng với HCl dư. Tổng thể tích H₂ (đktc) là?",options:["A. 2,24 lít","B. 3,36 lít","C. 4,48 lít","D. 5,60 lít"],a:"C. 4,48 lít",explain:"Fe: 0,1 mol → 0,1 mol H₂. Zn: 0,1 mol → 0,1 mol H₂. Tổng 0,2 mol → 4,48 lít.", level: "van_dung"},
{q:"Tên gọi của ester CH₃COOC₂H₅ theo IUPAC là?",options:["A. MEthyl ethanoate(CH3COOC2H5)","B. Ethyl ethanoate(CH3COOC2H5)","C. Methyl acetate","D. Ethyl acetate"],a:"B. Ethyl ethanoate(CH3COOC2H5)",explain:"Gốc acid: ethanoate, gốc alcohol: ethyl.", level: "nhan_biet"},
{q:"Để bảo vệ công trình thép khỏi gỉ, biện pháp thông dụng nhất là?",options:["A. Sơn phủ","B. Ngâm trong nước","C. Để ngoài trời mưa nắng","D. Phủ dầu mỏng"],a:"A. Sơn phủ",explain:"Lớp sơn cách li thép với oxygen và hơi nước.", level: "thong_hieu"},
{q:"Khi oxi hóa Ethyl alcohol (ethanol) bằng CuO nung nóng, sản phẩm thu được là?",options:["A. ethanal(CH3CHO)","B. Acetic acid","C. Ethylene","D. Ethane"],a:"A. ethanal(CH3CHO)",explain:"C₂H₅OH + CuO → CH₃CHO + Cu + H₂O.", level: "thong_hieu"},
{q:"Chất nào làm mất màu dung dịch KMnO₄ ở nhiệt độ thường?",options:["A. Ethyl alcohol (ethanol)","B. Acetic acid","C. Ethyl acetate","D. Nước"],a:"A. Ethyl alcohol (ethanol)",explain:"Ethyl alcohol (ethanol) bị oxi hóa bởi KMnO₄ làm mất màu.", level: "thong_hieu"},
{q:"Để tách riêng dầu ăn ra khỏi hỗn hợp với nước, phương pháp nào hiệu quả?",options:["A. Chiết","B. Chưng cất","C. Lọc","D. Kết tinh"],a:"A. Chiết",explain:"Dầu và nước không trộn lẫn, tách lớp, có thể chiết.", level: "van_dung"},
{q:"Cho 3,25 gam Zn tác dụng với 100 ml HCl 1M. Khối lượng muối ZnCl₂ thu được là?",options:["A. 4,08 gam","B. 6,80 gam","C. 8,16 gam","D. 10,20 gam"],a:"B. 6,80 gam",explain:"nZn = 0,05 mol, nHCl = 0,1 mol. Zn + 2HCl → ZnCl₂ + H₂. HCl hết. nZnCl₂ = 0,05 mol. M = 136 g/mol. m = 6,80 gam.", level: "van_dung"},
{q:"Ethanoic acid có thể phản ứng với chất nào tạo khí?",options:["A. Na₂CO₃","B. NaCl","C. NaOH","D. C₂H₅OH"],a:"A. Na₂CO₃",explain:"2CH₃COOH + Na₂CO₃ → 2CH₃COONa + CO₂↑ + H₂O.", level: "thong_hieu"},
{q:"Khi đốt cháy magnesium trong không khí, hiện tượng là gì?",options:["A. Cháy sáng chói, tạo bột trắng","B. Tan trong nước","C. Nổ","D. Không phản ứng"],a:"A. Cháy sáng chói, tạo bột trắng",explain:"2Mg + O₂ → 2MgO (bột trắng).", level: "thong_hieu"},
{q:"Để nhận biết ba chất: tinh bột, protein, chất béo, có thể dùng trình tự thuốc thử nào?",options:["A. Iodine → NaOH+CuSO₄ → tan trong nước","B. Bromine → Fehling → iodine","C. HCl → NaOH → Bromine","D. Nước → iodine → Bromine"],a:"A. Iodine → NaOH+CuSO₄ → tan trong nước",explain:"Iodine nhận tinh bột (xanh tím). Phản ứng biure (NaOH+CuSO₄) nhận protein (tím). Chất béo không tan trong nước, nổi.", level: "van_dung"},
{q:"Cho 0,1 mol kim loại M (hóa trị II) tác dụng với HCl dư thu được 0,1 mol H₂. M có hóa trị mấy?",options:["A. I","B. II","C. III","D. IV"],a:"B. II",explain:"Tỉ lệ M:H₂ = 1:1 → M + 2HCl → MCl₂ + H₂ → hóa trị II.", level: "van_dung"},
{q:"Khi cho potassium vào nước có phenolphtalein, hiện tượng là gì?",options:["A. Potassium tan, có khí, dung dịch hóa hồng","B. Potassium nổi, không tan","C. Có kim loại bám vào","D. Dung dịch chuyển màu tím"],a:"A. Potassium tan, có khí, dung dịch hóa hồng",explain:"K phản ứng với nước tạo KOH (base) làm phenolphtalein hóa hồng.", level: "thong_hieu"},
{q:"Polyethylene được điều chế bằng phản ứng gì?",options:["A. Trùng hợp","B. Trùng ngưng","C. Este hóa","D. Thế"],a:"A. Trùng hợp",explain:"nCH₂=CH₂ → (-CH₂-CH₂-)n (trùng hợp).", level: "nhan_biet"},
{q:"Để giảm ô nhiễm do khí thải xe máy, giải pháp khả thi là?",options:["A. Sử dụng xe điện hoặc nhiên liệu sạch hơn","B. Cấm tất cả xe máy","C. Chỉ cho chạy ban đêm","D. Pha thêm chì vào xăng"],a:"A. Sử dụng xe điện hoặc nhiên liệu sạch hơn",explain:"Xe điện và nhiên liệu sạch (xăng sinh học) giảm phát thải độc hại.", level: "van_dung"},
{q:"Khi cho khí chlorine ẩm vào dung dịch KI, hiện tượng là gì?",options:["A. Dung dịch chuyển màu vàng nâu","B. Có kết tủa trắng","C. Có khí thoát ra","D. Dung dịch mất màu"],a:"A. Dung dịch chuyển màu vàng nâu",explain:"Cl₂ + 2KI → 2KCl + I₂ (tan, màu vàng nâu).", level: "thong_hieu"},
{q:"Để chứng minh trong mẫu chất béo có glyxerol, có thể dùng phản ứng nào?",options:["A. Tạo phức xanh với Cu(OH)₂","B. Làm mất màu Bromine","C. Tạo màu tím với ninhydrin","D. Tạo kết tủa vàng với iodine"],a:"A. Tạo phức xanh với Cu(OH)₂",explain:"Glyxerol hòa tan Cu(OH)₂ tạo dung dịch xanh lam.", level: "van_dung"},
{q:"Kim loại nào có màu đỏ nâu?",options:["A. Nhôm","B. Copper","C. Iron","D. Bạc"],a:"B. Copper",explain:"Copper nguyên chất có màu đỏ nâu.", level: "nhan_biet"},
{q:"Khi cho NaOH vào dung dịch CuSO₄, hiện tượng là gì?",options:["A. Có kết tủa xanh lam","B. Có khí thoát ra","C. Dung dịch chuyển màu","D. Không hiện tượng"],a:"A. Có kết tủa xanh lam",explain:"2NaOH + CuSO₄ → Na₂SO₄ + Cu(OH)₂↓ (xanh lam).", level: "thong_hieu"},
{q:"Đốt cháy hoàn toàn 3,6 gam Mg trong O₂ dư. Khối lượng MgO thu được là?",options:["A. 4,0 gam","B. 5,0 gam","C. 6,0 gam","D. 7,0 gam"],a:"C. 6,0 gam",explain:"2Mg + O₂ → 2MgO. nMg = 0,15 mol → nMgO = 0,15 mol → m = 0,15×40 = 6,0 gam.", level: "van_dung"},
{q:"Tại sao người ta thường dùng Copper hoặc nhôm làm dây điện mà không dùng Iron?",options:["A. Iron dẫn điện kém hơn, nặng, dễ gỉ","B. Iron đắt hơn","C. Iron không kéo thành sợi","D. Iron không có ánh kim"],a:"A. Iron dẫn điện kém hơn, nặng, dễ gỉ",explain:"Copper, nhôm dẫn điện tốt, nhẹ (nhôm), ổn định hơn.", level: "thong_hieu"},
{q:"Khi cho Bromine vào dầu ăn, màu Bromine nhạt dần vì sao?",options:["A. Dầu ăn chứa acid béo không no","B. Dầu ăn chứa glyxerol","C. Dầu ăn chứa protein","D. Dầu ăn chứa đường"],a:"A. Dầu ăn chứa acid béo không no",explain:"Nối đôi trong acid béo không no cộng Bromine.", level: "van_dung"},
{q:"Cho 4,8 gam Mg vào 200 ml HCl 1M. Thể tích H₂ (đktc) thu được là?",options:["A. 2,24 lít","B. 3,36 lít","C. 4,48 lít","D. 1,12 lít"],a:"A. 2,24 lít",explain:"nMg = 0,2 mol, nHCl = 0,2 mol. HCl thiếu. nH₂ = nHCl/2 = 0,1 mol. V = 2,24 lít.", level: "van_dung"}
];

